{function noty(t){new Noty({theme:"relax",text:t,type:"success",layout:"topRight",timeout:1500}).show()}var createPost=function(){var t=$("#new-post-form");t.submit((function(e){e.preventDefault(),$.ajax({type:"post",url:"posts/create",data:t.serialize(),success:function(t){var e=newPostDOM(t.data);noty(t.message),deletePost($(" .delete-post-button",e)),new ToggleLike($(" .toggle_likes_button",e)),$("#posts-list-container>ul").prepend(e)},error:function(t){console.log(t.responseText)}})}))},newPostDOM=function(t){return $(`<li id="post-${t.post._id}">\n                        <small>\n                            <a class="delete-post-button" href="/posts/destroy/${t.post._id}">X</a>\n                        </small>\n                    ${t.post.content}<br>\n                    <small>${t.user}</small>\n                    <small><a class="toggle_likes_button" data-likes="0" href="/likes/toggle/?id=${t.post._id}&type=Post">0 Likes</a></small>\n                    <div id="post-comments">\n                        <form action="/comments/create" method="post">\n                        <input type="text" name="content" placeholder="Comment... " required>\n                        <input type="hidden" name="post" value="${t.post._id}">\n                        <input type="submit" value="Add Comment">\n                        </form>\n                    </div>\n\n                </li>`)},deletePost=function(t){t.click((function(e){e.preventDefault(),$.ajax({type:"get",url:$(t).prop("href"),data:$(t).prop("postid"),success:function(t){$("#post-"+t.post_id).remove(),noty(t.message)},error:function(t){console.log(t.responseText)}})}))};createPost()}